package proram;

import java.util.Scanner;

public class odd {
	
	public static void main(String[] args) {
		// TODO Auto-generated method stub
   
		Scanner sc =new Scanner(System.in);
		 
		int a=sc.nextInt();
		
		if(a%2==0) {
			System.out.println("the no is even"); 
		}
		else { 
			System.out.println("the no is odd");
		}
		
	}

}
